import React , {Component} from 'react';


class ErrorBoundry extends Component{
	constructor(props){
		super(props);
		this.state = {
			hasError: false
		}
	}

	render(){
		if (this.state.hasError) {
			return '<h1>Opps got some error</h1>'
		}
		return props.children
	}
}

export default ErrorBoundry;
